# Відкриваємо файл і читаємо всі рядки
file_path = '/Users/furmonenko/Desktop/eu5-ukrainian-localization/main_menu/localization/english/advances_l_english.yml'

with open(file_path, 'r', encoding='utf-8-sig') as f:
    lines = f.readlines()

# Замінюємо перекладені рядки
for i, line in enumerate(lines):
    for key, translation in translations.items():
        # Шукаємо рядки з ключем
        if line.strip().startswith(f'{key}:'):
            # Зберігаємо відступ
            indent = len(line) - len(line.lstrip())
            lines[i] = ' ' * indent + f'{key}: "{translation}"\n'
            break

# Записуємо результат назад у файл
with open(file_path, 'w', encoding='utf-8-sig') as f:
    f.writelines(lines)

print("Переклад рядків 2600-2800 завершено!")
