def main():
    """–ì–æ–ª–æ–≤–Ω–∞ —Ñ—É–Ω–∫—Ü—ñ—è"""
    import sys

    file_path = '/Users/furmonenko/Desktop/eu5-ukrainian-localization/main_menu/localization/english/advances_l_english.yml'

    print("üîÑ –ü—Ä–æ–¥–æ–≤–∂–µ–Ω–Ω—è –ø–µ—Ä–µ–∫–ª–∞–¥—É advances_l_english.yml (—á–∞—Å—Ç–∏–Ω–∞ 2)")
    print("=" * 70)

    try:
        with open(file_path, 'r', encoding='utf-8-sig') as f:
            lines = f.readlines()

        translations = get_translations()
        count = 0

        for i in range(2999, min(4000, len(lines))):
            line = lines[i]

            if ':' not in line or not line.strip() or line.strip().startswith('#'):
                continue

            key = line.split(':', 1)[0].strip()

            if key in translations:
                indent = len(line) - len(line.lstrip())
                lines[i] = ' ' * indent + key + ': "' + translations[key] + '"\n'
                count += 1
                print(f"‚úì {key}")

        with open(file_path, 'w', encoding='utf-8-sig') as f:
            f.writelines(lines)

        print("\n" + "=" * 70)
        print(f"‚úÖ –£–°–ü–Ü–®–ù–û –ü–ï–†–ï–ö–õ–ê–î–ï–ù–û (—á–∞—Å—Ç–∏–Ω–∞ 2): {count} —Ä—è–¥–∫—ñ–≤")
        print("=" * 70)

    except Exception as e:
        print(f"\n‚ùå –ü–û–ú–ò–õ–ö–ê: {e}")
        sys.exit(1)

if __name__ == '__main__':
    main()
