    # Обробляємо рядки
    print("Обробка блоку 3...")

    modified_count = 0
    for i in range(len(lines)):
        line = lines[i]

        # Пропускаємо порожні рядки та коментарі
        if not line.strip() or line.strip().startswith('#'):
            continue

        # Шукаємо ключ в рядку
        match = re.match(r'^(\s*)([a-z_0-9]+):\s*"(.+)"', line)
        if match:
            indent = match.group(1)
            key = match.group(2)
            old_value = match.group(3)

            # Якщо є переклад для цього ключа
            if key in translations:
                new_value = translations[key]
                lines[i] = f'{indent}{key}: "{new_value}"\n'
                modified_count += 1
                if modified_count % 50 == 0:
                    print(f"  Оброблено {modified_count} рядків...")

    # Записуємо назад
    with open(file_path, 'w', encoding='utf-8-sig') as f:
        f.writelines(lines)

    print(f"\n✅ Готово! Перекладено рядків: {modified_count}")
    print(f"Файл: {file_path}")

